    function createPopUp(currentFeature) {
      var popUps = document.getElementsByClassName('mapboxgl-popup');
      var wikiURL = 'https://' + language + '.wikipedia.org/api/rest_v1/page/summary/';
      var wikipageContent = wikiURL + currentFeature.properties.Name;

      var getJSON = function ( url, callback ) {

        // Feature detection
        if ( !window.XMLHttpRequest ) return;

        // Create new request
        var xhr = new XMLHttpRequest();

        // Setup callback
        xhr.onload = function() {
          if ( callback && typeof( callback ) === 'function' ) {
            callback( this.response );
          }
        }

        // Get the HTML
        xhr.open( 'GET', url );
        xhr.responseType = 'json';
        xhr.send();

      };

      getJSON( wikipageContent, function (response) {
        var summary = response.extract;
        var wikiLink = 'https://' + language + '.wikipedia.org/wiki/' + currentFeature.properties.Name;
        var title = '<div class="title">' + currentFeature.properties.Name + '</div>';
        var content = '';
        if (response.title !== "Not found.") {
          content = '<div class="content">' + summary + '</div>' +
            '<a class="wikiLink" target="_blank" href="' + wikiLink + '">Wiki →</a>'
        }

        if (currentFeature.properties.historic === 'memorial') {
          title = '<div class="title-memorial">' + currentFeature.properties.Name + ' [Memorial]</div>';
        }

        // Check if there is already a popup on the map and if so, remove it
        if (popUps[0]) popUps[0].remove();

        var popup = new mapboxgl.Popup({ closeOnClick: false })
          .setLngLat(currentFeature.geometry.coordinates)
          .setHTML('<div class="popupContainer">' + title + content + '</div')
          .addTo(map);
        });
    }

